import { NgxSpinnerService } from 'ngx-spinner';
import { NotificationsService } from 'angular2-notifications';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiClientService } from 'src/app/services/api-client.service';
declare var $: any;

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.scss']
})
export class SignUpComponent implements OnInit {
  idPartner: string="";
  view = 'noLogin';
  tab = "donante"; // direccion - donante - completo
  terminos = false;
  options = {
    position: ['bottom', 'right'],
    timeOut: 5000,
    lastOnBottom: true,
    showProgressBar: true,
    pauseOnHover: true,
    clickToClose: true,
  };

  imgLoaded: any = "data:image/png;base64,";

  personForm: FormGroup = new FormGroup({
    // image: new FormControl('', [Validators.required]),
    name: new FormControl('', [Validators.required]),
    phone: new FormControl('', [Validators.required]),
    cell_phone: new FormControl('', [Validators.required]),
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', [Validators.required]),
    password2: new FormControl('', [Validators.required])
  });

  addressForm: FormGroup = new FormGroup({
    text: new FormControl('', [Validators.required]),
    postal_code: new FormControl('', [Validators.required]),
    street: new FormControl('', [Validators.required]),
    street_line: new FormControl('', [Validators.required]),
    city: new FormControl('', [Validators.required]),
    state: new FormControl('', [Validators.required]),
  });


  submitted = false;
  submitted2 = false;

  constructor(private spinner: NgxSpinnerService, private route: ActivatedRoute, private router: Router, private apiClient: ApiClientService, private notify: NotificationsService) { 
    this.idPartner = this.route.snapshot.params.idPartner;
  
  }

  ngOnInit(): void {

  }

  get f() {
    return this.personForm.controls;
  }

  get f2() {
    return this.addressForm.controls;
  }

  registrarme() {
    this.submitted = true;
    this.personForm.value.image = this.imgLoaded;
    console.log(this.personForm.value);
    if (this.personForm.valid) {
      if (this.personForm.value.password == this.personForm.value.password) {
        if (this.validadorPasword()[0] && this.validadorPasword()[1] && this.validadorPasword()[2]) {
          this.tab = 'direccion';
        }
      }
    }
  }

  registrarAddress() {
    this.submitted2 = true;
    console.log(this.addressForm.value);
    if (this.addressForm.valid) {
      if (this.terminos == true) {
        this.spinner.show();

        let data = {
          image: this.imgLoaded,
          ...this.personForm.value,
          address: this.addressForm.value
        };

        this.apiClient.create(data).subscribe(
          response => {
            this.spinner.hide();
            this.tab = 'completo';
            setTimeout(() => {
              if(this.idPartner==''){
                this.router.navigate(['sign-up-confirm']); 
              }
              else{
                // this.router.navigateByUrl(`amount-donation/${this.idPartner}`);
                this.router.navigateByUrl(`login`);
              }   
            }, 10000);
          },
          error => {
            this.spinner.hide();
            this.notify.warn('¡Advertencia!', 'La cuenta no pudo ser creada.');
          });
      }
    }
  }

  cargarImagen(event) {
    for (let d of event.target.files) {
      let file = d;
      console.log(file.name);
      let reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        this.imgLoaded = reader.result.toString();
        console.log(this.imgLoaded);
      };
    }
  }

  validadorPasword(): boolean[] {
    let respuesta: boolean[] = [false, false, false];
    var texto = this.personForm.value.password;
    for (let index = 0; index < texto.length; index++) {
      let letra = texto.charAt(index);
      let letras = "ABCDEFGHIJKLMNÑOPQRSTUVWXYZ";
      for (let i = 0; i < letras.length; i++) {
        if (letra == letras[i]) {
          respuesta[0] = true;
        }
      }
      let numberChars = "1234567890";
      for (let i = 0; i < numberChars.length; i++) {
        if (letra == numberChars[i]) {
          respuesta[1] = true;
        }
      }
      let specialChars = "!@#$^&%*()+=-[]\/{}|:<>?,.";
      for (let i = 0; i < specialChars.length; i++) {
        if (letra == specialChars[i]) {
          respuesta[2] = true;
        }
      }
    }
    return respuesta;
  }

  onSaveUsernameChanged(value: boolean) {
    this.terminos = value;
  }


  regresar(){
    console.log(this.idPartner);
    if(this.idPartner==undefined){
      this.router.navigate(['login']);
    }
    else{
      this.router.navigate(['home']);
    }
  }

}
